prototype.update:
const padding = getConfig(id)[getSetting(theme).padding_name];
let window = group.getWindow("header");
const _padding = window.location.globalToWindow(padding);
let content = window.getContent();

content.drawing[1].x = content.drawing[1].x || 0;
content.drawing[1].x += _padding;
content.drawing[1].width -= _padding * 2;
content.elements["default-close-button"].x -= _padding;

group.setWindowContent("header", content);
let names = ["main", "tabs"];
for(let i in names){
	let name = names[i];
	window = group.getWindow(name);
	content = window.getContent();

	content.location.x += padding;
	content.location.width -= padding * 2;

	group.setWindowContent(name, content);
}

prototype.updateSize:
const padding = getConfig(id)[getSetting(theme).padding_name];
result.x += padding;
result.width -= padding;
